<script>
import axios from "axios";
//import VideoPlayer from "../components/VideoPlayer.vue";
import "video.js/dist/video-js.css";
import "../../public/exported_styles/vote.css";

export default {
  name: "Votes",
  components: {
    //VideoPlayer,
  },
  data() {
    return {
      loadError: false,
      loadErrorMessge: "",
      videos: [],
      voteInfo: [],
      voteInfoVideoIds: [],
      videoOptions: {},
      num: 0,
      votingData: {},
    };
  },
  async created() {
    const voteInfo = await axios.get(
      "http://localhost:3000/getall/current-vote-count"
    );
    if (voteInfo.status !== 200) {
      this.loadError = true;
      this.loadErrorMessge = "Error loading vote information";
    } else {
      try {
        this.voteInfo = voteInfo.data;
        voteInfo.data.forEach((item) =>
          this.voteInfoVideoIds.push(item.muxVideoId)
        );
      } catch (error) {
        console.log(error);
      }
    }

    const allVideos = await axios.get("http://localhost:3000/getall/allvideos");
    try {
      allVideos.data.forEach((video) => {
        const currentPlaybackId = video.muxVideoId;
        this.videos.push({
          src: `https://stream.mux.com/${currentPlaybackId}.m3u8`,
          thumbnail: `https://image.mux.com/${currentPlaybackId}/thumbnail.jpg`,
          type: "application/x-mpegURL",
          voteTally: video.voteTally,
          newVotes: 0,
          id: currentPlaybackId,
        });
      });
    } catch (error) {
      this.loadError = true;
      this.loadErrorMessge = "Error loading videos";
      console.log(error);
    }

    // this.videos.forEach((video) => {
    //   this.votingData[video.id] = {
    //     voteTally: video.voteTally,
    //   };
    // });
  },

  methods: {
    changeVoteCount(operator = null, number, id) {
      if (operator !== null) {
        if (operator === "minus") {
          number--;
        } else if (operator === "plus") {
          number++;
        }
      }

      this.videos.forEach((video) => {
        console.log(video.id);
        if (video.id === id) {
          console.log(number);
          video.newVotes = number;
        }
        console.log(this.videos);
      });
    },
    submitStripePayment() {
      axios.post("http://localhost:3000/post/create-checkout-session");
    },
  },
};
</script>

